import axios from "axios";
import { useEffect, useState } from "react";


function ListComment(props){
	const cmt = props.arr;
	console.log(cmt)

	function handle(e){
		e.preventDefault();
		let id = e.target.id;
		props.getId(id);	
	}

	function renderData(){
		if((cmt).length > 0){
			return (cmt).map((value,key)=>{
				if(value.id_comment == 0){
					return(
					<div>
						<li key={key} class="media second-media">
								<a class="pull-left" href="#">
									<img class="media-object" src="" alt=""/>
								</a>
								<div class="media-body">
									<ul class="sinlge-post-meta">
										<li><i class="fa fa-user"></i>{value.name_user}</li>
										<li><i class="fa fa-clock-o"></i>1:33 PM</li>
										<li><i class="fa fa-calendar"></i> {value.updated_at}</li>
									</ul>
									<p>{value.comment}</p>
								    <a onClick={handle} class="btn btn-primary" id={value.id_user} href="#cmt"><i class="fa fa-reply"></i>Replay</a>
								</div>
						</li>

							{cmt.map((value,key)=>{
								if(value.id_comment != 0 && value.id_comment == value.idcha )// level.map2 = id.map1
								return (
								<li key={key} class="media second-media">
									<a class="pull-left" href="#">
										<img class="media-object" src="" alt=""/>
									</a>
									<div class="media-body">
										<ul class="sinlge-post-meta">
											<li><i class="fa fa-user"></i>{value.name_user}</li>
											<li><i class="fa fa-clock-o"></i>1:33 PM</li>
											<li><i class="fa fa-calendar"></i> {value.updated_at}</li>
										</ul>
										<p>{value.comment}</p>
										
									</div>
						        </li>
								)
							})
						    }
						
					 </div>
					)
				}
					
		})
	}
	
	}
	return (
		<div class="response-area">
			<h2>3 RESPONSES</h2>
			<ul class="media-list">
				{renderData()}
			</ul>					
	    </div>
	)
}
export default ListComment;
