import axios from "axios";
import { useEffect, useState } from "react";
import React from "react";
function Add_product(){

const [Category, setCategory] = useState();
const [Brand, setBrand] = useState();

console.log(Category)

useEffect(()=>{
    axios.get("http://localhost:8080/laravel8/laravel8/public/api/category-brand")
    .then(res=>{
        console.log(res)
        setCategory(res.data)
        setBrand(res.data.brand)

    })
    .catch(function (error){
        console.log(error)
        
    })
},[]);
function handleSubmit(e){
    e.preventDefault();

}
function showData(){
    if(Object.keys(Category).length > 0){
        return (Object.keys(Category)).map((value,key)=>{
         return(   
            <select >
                <option value={value["id"]}>{value["category"]}</option>
            </select>
         )
        })
    }
}
// function handleIp(e){{
//     let getName= e.target.name
//     let getValue = e.target.value
//     setData(state => ({...state,[getName]:getValue}))
// }}

    return(
        <div>
            <h2>Creat Product</h2>
            <form onSubmit={handleSubmit}>
                <p><input placeholder="name"></input></p>
                <p><input placeholder="price"></input></p>
                <p><input placeholder="price"></input></p>
                <p>
                   {showData()}
                </p>
            </form> 
            <button type="submit">Signup</button>
        </div>
    )
}
export default Add_product;
