import { isValidInputTimeValue } from "@testing-library/user-event/dist/utils";
import axios from "axios";
import { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { UserContext } from "../../UserContext";
function Wishlist(){
    const [data,setData] = useState([]);
    const use1 = useContext(UserContext);
    // console.log(data)
    useEffect(()=>{
        axios.get("http://localhost:8080/laravel8/laravel8/public/api/product/wishlist")
        .then(res=>{
            setData(res.data.data)
        });
    },[]);
    
    let Obj = localStorage.getItem("W_list");
        Obj = JSON.parse(Obj);
    const idObj = Object.keys(Obj);
    // if(data.length > 0){
    //    data.map((value,key)=>{
    //    let idPr = data[key]["id"];
    const filteredItems = data.filter(item => Obj[idObj]);

    console.log(filteredItems);

    //    })
    // }    



    let WL = {};
    function tongWL(){
        let totalWL = 0;
        let getQty = localStorage.getItem("W_list")
            if(getQty){
                WL=JSON.parse(getQty)
                Object.keys(WL).map(function(key){
                    totalWL += WL[key];
                })
            }
            // console.log(totalWL)
            localStorage.setItem("W_list", JSON.stringify(WL));	
        use1.Wl(totalWL);
    }

    function XoaWL(e){
        e.preventDefault();
        let getid= e.target.id;
        let newData = [...data];
        if(newData.length > 0){
            newData.map((value,key)=>{
                if(value.id == getid){
                   delete newData[key];
                }
            })
        }
        let newArray = newData.filter(function(v){return v!==''})
        setData(newArray); 
    // local:
        let W_list = localStorage.getItem("W_list");
        if(W_list){
            WL = JSON.parse(W_list)
            if(WL[getid]){
                delete WL[getid];
            }
        }
        localStorage.setItem("W_list", JSON.stringify(WL));  
        tongWL();
    }

    
    function renderData(){
        if(data.length > 0){
            return data.map((value,key)=>{
                let images = data[key]["image"];
                let img = JSON.parse(images);
                return(
                    <div class="col-sm-4">
                        <div class="product-image-wrapper">
                            <div class="single-products">
                                <div class="productinfo text-center">
                                                <img src={"http://localhost:8080/laravel8/laravel8/public/upload/product/" + data[key]["id_user"] + "/" + img[0] } alt="" />
                                                <h2>{data[key]["price"]}</h2>
                                                <p>{data[key]["name"]}</p>
                                                <a href="" class="btn btn-default add-to-cart"><i class="fa fa-shopping-cart"></i>Add to cart</a>
                                </div>
                                <div class="product-overlay">
                                    <div class="overlay-content">
                                        <h2>{data[key]["price"]}</h2>
                                        <p>{data[key]["name"]}</p>
                                        <a href="" class="btn btn-default add-to-cart"><i class="fa fa-shopping-cart"></i>Add to cart</a>
                                    </div>
                                </div>
                            </div>
                            <div class="choose">
                                <ul class="nav nav-pills nav-justified">
                                    <li><a onClick={XoaWL} id={data[key]["id"]} ><i class="fa fa-plus-square"></i>Delete to wishlist</a></li>
                                    <li><a href="#"><i class="fa fa-plus-square"></i>Add to compare</a></li>
                                    <li><Link to={"http://localhost:3000/products/detail/" + data[key]["id"] }>More</Link></li>
                                </ul>
                            </div>
                        </div>
                    </div>
                    
                )
            })
        }
    }
    return(
        <div class="col-sm-9 padding-right">
        <div class="features_items">
        <h2 class="title text-center">Features Items</h2>
        {renderData()}
        </div>
    </div>
    )
}
export default Wishlist;
