import axios from "axios";
import { useEffect, useState } from "react";
import { Link, json } from "react-router-dom";
function Products(){

const [data,setData] = useState([]);
const [cart,setCart] = useState({});
useEffect(()=>{
    axios.get("http://localhost:8080/laravel8/laravel8/public/api/product/list")
    .then(res=>{
        // console.log(res)
        setData(res.data.data.data)
    });
	const local = localStorage.getItem("cart");
	const checklocal = JSON.parse(local);
	setCart(checklocal)
},[]);


function handleAdd_cart(e){
	e.preventDefault(); 
	const id = e.target.id;
	const checkCart = cart[id];
	if(checkCart){
		const updatedCart = {
			...cart,
			[id]: { id: id, quantity: checkCart.quantity + 1 }
		  };
		  setCart(updatedCart);
	}else{
		const newCart = { id: id, quantity: 1 };
		const updatedCart = {
		  ...cart,
		  [id]: newCart
		};
		setCart(updatedCart);
	}
}
useEffect(() => {
    // Lưu thông tin giỏ hàng vào localStorage khi cartItems thay đổi
    localStorage.setItem('cartItems', JSON.stringify(cart));
  }, [cart]);

 

function renderData(){
	if(data.length > 0){
		return data.map((value,key)=>{
			let images = data[key]["image"];
			let img = JSON.parse(images);
			return(
				<div class="col-sm-4">
					<div class="product-image-wrapper">
						<div class="single-products">
							<div class="productinfo text-center">
											<img src={"http://localhost:8080/laravel8/laravel8/public/upload/product/" + data[key]["id_user"] + "/" + img[0] } alt="" />
											<h2>{data[key]["price"]}</h2>
											<p>{data[key]["name"]}</p>
											<a onClick={handleAdd_cart} id={data[key]["id"]} href="" class="btn btn-default add-to-cart"><i class="fa fa-shopping-cart"></i>Add to cart</a>
							</div>
							<div class="product-overlay">
								<div class="overlay-content">
									<h2>{data[key]["price"]}</h2>
									<p>{data[key]["name"]}</p>
									<a onClick={handleAdd_cart} id={data[key]["id"]} href="" class="btn btn-default add-to-cart"><i class="fa fa-shopping-cart"></i>Add to cart</a>
								</div>
							</div>
						</div>
						<div class="choose">
							<ul class="nav nav-pills nav-justified">
								<li><a href="#"><i class="fa fa-plus-square"></i>Add to wishlist</a></li>
								<li><a href="#"><i class="fa fa-plus-square"></i>Add to compare</a></li>
								<li><Link to={"http://localhost:3000/products/detail/" + data[key]["id"] }>More</Link></li>
							</ul>
						</div>
					</div>
				</div>
				
			)
		})
	}
}


    return(
        <div class="col-sm-9 padding-right">
            <div class="features_items">
            <h2 class="title text-center">Features Items</h2>
            {renderData()}
            </div>
        </div>

    )
}
export default Products;
