import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Edit_product(){

const param = useParams();
let token = localStorage.getItem("token");
const [data,setData] = useState({
    Name:"",
    Price:"",
    Category:"",
    Brand:"",
    Status:"",
    Company:"",
    Detail:"",
    Sale:"",
    Image:""

});
useEffect(()=>{
    let config = {
        headers: {
            'Authorization': 'Bearer '+ token,
            'Content_Type': 'multipart/form/data',
            'Accept': 'application/json'
        }
    };
    axios.get("http://localhost:8080/laravel8/laravel8/public/api/user/product/" + param.id, config)
    .then(res=>{
        console.log(res)
        let getdata = res.data.data;
        setData({
            Name: getdata.name,
            Price: getdata.price,
            Category: getdata.category,
            Brand: getdata.brand,
            Status: getdata.status,
            Company: getdata.company,
            Detail: getdata.detail,
            Image: getdata.image,
            Sale: getdata.sale ? getdata.sale : 0,
        })
    });
},[]);




return(
    <div class="signup-form">
        <h2>Creat Product</h2>
        <form >
            <p><input type="text" value={data.Name} placeholder="Name"></input></p>
            <p><input type="text" placeholder="Price"></input></p>
            <p><input type="text" placeholder="Category"></input></p>
            <p><input type="text" placeholder="Brand"></input></p>
            <p><input type="text" placeholder="Status"></input></p>
            <p><input type="text" placeholder="Company"></input></p>
            <p><input type="text" placeholder="Detail"></input></p>
            {/* <p><input placeholder="Sale"></input></p> */}
        </form>
    </div>
)


}
export default Edit_product;
