import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function My_Product(){
let token = localStorage.getItem("token");
let id = localStorage.getItem("auth");
    id = JSON.parse(id)
    let id_user = id.id;
const [data,setData] = useState({});

function hanldeDelete(e){
        let id_pro =  e.target.id;
        console.log(id_pro);
        let config = {
            headers: {
                'Authorization': 'Bearer '+ token,
                'Content_Type': 'multipart/form/data',
                'Accept': 'application/json'
            }
        };
        let formData = new FormData();
            formData.append('id', id_pro);
        axios.post("http://localhost:8080/laravel8/laravel8/public/api/user/product/delete/" + id_user, config, formData)   
    
}


function renderData(){
    if(Object.keys(data).length > 0){
        return Object.keys(data).map((key,index)=>{
            let images = data[key]["image"];
                images = JSON.parse(images);
            let id_pro = data[key]["id"];
            console.log(id_pro)
            return(   
                <tr>
                    <td width={100}>{data[key]["id"]}</td>
                    <td width={100}>{data[key]["name"]}</td>
                    <td width={100}><img width={70} height={70} src={"http://localhost:8080/laravel8/laravel8/public/upload/product/" + data[key]["id_user"] + "/" + images} alt=""></img></td>
                    <td width={100}>{data[key]["price"]}</td>
                    <td><a>Edit</a></td>
                    <td id={data[key]["id"]} onClick={hanldeDelete}>delete</td>    
                </tr>
            )
        })
    }
}
useEffect(()=>{
    let config = {
        headers: {
            'Authorization': 'Bearer '+ token,
            'Content_Type': 'multipart/form/data',
            'Accept': 'application/json'
        }
    };
    axios.get("http://localhost:8080/laravel8/laravel8/public/api/user/my-product", config)
    .then(res=>{
        setData(res.data.data)
        console.log(res)
    });
},[]);

    return(
        <div>
            <table>
            <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Image</th>
                <th>Price</th>
                <th>Action</th>
            </tr>
            {renderData()} 
            </table>
        <p><Link to={"http://localhost:3000/account/myproduct/addproduct"} >Add</Link></p>
        </div>
    )
}
export default My_Product;
