}


return(
    <div class="signup-form">
        <h2>Edit Product</h2>
        <form >
            <p><input type="text" value={data.Name} placeholder="Name" onChange={handleIP}></input></p>
            <p><input type="text" value={data.Price} placeholder="Price" onChange={handleIP}></input></p>
            <p><input type="text" value={data.Category} placeholder="Category" onChange={handleIP}></input></p>
            <p><input type="text" value={data.Brand} placeholder="Brand" onChange={handleIP}></input></p>
            <p><input type="file" multiple></input></p>
            {renderImg()}
            <p><input type="text" value={data.Status} placeholder="Status" onChange={handleIP}></input></p>
            <p><input type="text" value={data.Company} placeholder="Company" onChange={handleIP}></input></p>
            <p><input type="text" value={data.Detail} placeholder="Detail" onChange={handleIP}></input></p>
