import axios from "axios";
import { useEffect, useState } from "react";
import React from "react";
function Add_product(){

const [Category, setCategory] = useState({});
const [Brand, setBrand] = useState([]);
const [input,setInput] = useState({
    name:"",
    price:"",
    component:"",
    detail:"",
    category:"",
    brand:""
})

useEffect(()=>{
    axios.get("http://localhost:8080/laravel8/laravel8/public/api/category-brand")
    .then(res=>{
        // console.log(res.data.category)
        setCategory(res.data.category)
        setBrand(res.data.brand)

    })
    .catch(function (error){
        console.log(error)
        
    })
},[]);

    const [err,setErr] = useState("");
    function handleSubmit(e){
        e.preventDefault();
        let errObj = {};
        let x =1;
        if(input.name == ""){
            errObj.name = "vui long nhap ten";
            x = 2;
        }
        if(input.price == ""){
            errObj.name = "vui long nhap gia";
            x = 2;
        }
        if(input.select == ""){
            errObj.select = "please choose category";
            x = 2;
        }
        if(input.brand == ""){
            errObj.brand = "please choose brand";
            x = 2;
        }
        if(input.component){
            errObj.component = "vui long nhap thanh phan";
            x = 2;
        }
        if(input.detail){
            errObj.detail = "vui long nhap chi tiet";
            x = 2;
        }
        setErr(errObj);
        if(x==1){
            alert("ok")
        }
    } 

    function handleInput(e){
        let getName= e.target.name
        let getValue = e.target.value
        setInput(state => ({...state,[getName]:getValue}))
        
    }
    
    function showSale(){
        if(input ){
            return(
                <input placeholder="sale"></input>
            )
        }
    }

function showSelect(){
    if((Category).length > 0){
        return (Category).map((value,key)=>{
            return(
                <option value={value.id}>{value.category}</option>
            )
        })         
    }
};
function showBrand(){
    if((Brand).length > 0){
        return (Brand).map((value,key)=>{
            return(
                <option value={value.id}>{value.brand}</option>
            )
        })         
    }
};
function showErr(){
    if(Object.keys(err).length > 0){
        return Object.keys(err).map((key,index)=>{
          return(
            <li key={key}>
              {err[key]}
            </li>
          )
        })
      }
}

    return(
        <div class="signup-form">
            <h2>Creat Product</h2>
            <form onSubmit={handleSubmit} enctype="multipart/form-data">
                {showErr()}
                <p><input type="text" placeholder="name" name="name" onChange={handleInput}></input></p>
                <p><input type="text" placeholder="price" name="price" onChange={handleInput}></input></p>
                <p>
                    <select name="category">
                        <option>Vui long chon category</option>
                        {showSelect()}
                    </select>
                </p>
                <p>
                    <select name="brand">
                        <option>Vui long chon brand</option>
                        {showBrand()}
                    </select>
                </p>
                   
                <p><input type="file" multiple></input></p>
                <p>
                    <select onChange={handleInput} name="select">
                        <option value={1}>New</option>
                        <option value={0}>Sale</option>
                    </select>
                <p>{showSale()}</p>
                </p>
                <p><input type="text" placeholder="Component" name="component" onChange={handleInput}></input></p>
                <p><input type="text" placeholder="Detail" name="detail" onChange={handleInput}></input></p>

            </form> 
            <button type="submit">Signup</button>
        </div>
    )
}
export default Add_product;
