const [data, setData] = useState({
    message:""
});
const [err,setErr] = useState([]);
let errObj = {}
useEffect(()=>{
    axios.get("http://localhost:8080/laravel8/laravel8/public/api/blog/comment/id")
    .then(res =>{
        console.log(res)

    })
})
function handleIP(e){
	let getName= e.target.name
		  let getValue = e.target.value
		  setData(state => ({...state,[getName]:getValue}))
}

function handleSubmit(){
if(xx == null){
errObj.Comment = "vui long dang nhap"
}
if(data.message == ""){
errObj.message = "vui long nhap binh luan"
}
setErr(errObj)
}

function showErr(){
    if(Object.keys(err).length > 0){
        return Object.keys(err).map((key,index)=>{
          return(
            <li key={key}>
              {err[key]}
            </li>
          )
        })
      }

}
function renderData(){
    return(
        <div class="text-area">
            <div class="blank-arrow">
                <label>Your Name</label>
            </div>
            <span>*</span>
            <form onSubmit={handleSubmit}>
             {showErr()}   
            <textarea name="message" rows="11" onChange={handleIP}></textarea>
            <button class="btn btn-primary" href="">Post comment</button>
            </form>
        </div>
    )
}
    return(
        <div class="replay-box">
            <div class="row">
                <div class="col-sm-12">
                    <h2>Leave a replay</h2>
                    {renderData()}
                </div>
            </div>
        </div>
    )
}
export default Comment;
