import axios from "axios";
import { useEffect, useState } from "react";

function Cart(){
let cart = localStorage.getItem("cart");
    cart = JSON.parse(cart);

const [data,setData] = useState([]);

useEffect(()=>{
    axios.post("http://localhost:8080/laravel8/laravel8/public/api/product/cart", cart)
    .then(res=>{
        setData(res.data.data)
        // console.log(res.data.data)
    })
},[]);

// function handleCong(e){
//     e.preventDefault();
//     let id = e.target.id;
//     if(data.length > 0){
//         const updatedData = data.map((value,key)=>{
//             if(value.id == id){
//                 return { ...value, qty: value.qty + 1 };
//             }   
//             return value; 
//         }) 
//         setData(updatedData); 
//     }     
// }
// function handleTru(e){
//     e.preventDefault();
//     let id = e.target.id;
//     if(data.length > 0){
//         const updatedData1 = data.map((value,key)=>{
//             if(value.id == id){
//                 return { ...value, qty: value.qty - 1 };
//             }   
//             return value; 
//         }) 
//         setData(updatedData1);
//     }  
// }
       
function renderData(){
    if(data.length > 0){
        return data.map((value,key)=>{
            let img = value.image;
            let images= JSON.parse(img);
            let total = value.price * value.qty;
            return(
                <tr>
                    <td class="cart_product">
                        <a href=""><img width={70} height={50} src={"http://localhost:8080/laravel8/laravel8/public/upload/product/" + value.id_user + "/" + images} alt=""/></a>
                    </td>
                    <td class="cart_description">
                        <h4><a href="">{value.name}</a></h4>
                        <p>Web ID: 1089772</p>
                    </td>
                    <td class="cart_price">
                        <p>{value.price}</p>
                    </td>
                    <td class="cart_quantity">
                        <div class="cart_quantity_button">
                            <a onClick={handleCong} id={value.id} class="cart_quantity_up" > + </a>
                            <input class="cart_quantity_input" type="text" value={value.qty} autocomplete="off" size="2"/>
                            <a onClick={handleTru} id={value.id} class="cart_quantity_down"> - </a>
                        </div>
                    </td>
                    <td class="cart_total">
                        <p class="cart_total_price">{total}</p>
                    </td>
                    <td class="cart_delete">
                        <a  class="cart_quantity_delete" href=""><i class="fa fa-times"></i></a>
                    </td>
                </tr>
               
            )
        })
    }
}

return(
    <section id="cart_items">
    <div class="container">
        <div class="breadcrumbs">
            <ol class="breadcrumb">
              <li><a href="#">Home</a></li>
              <li class="active">Shopping Cart</li>
            </ol>
        </div>
        <div class="table-responsive cart_info">
        <table class="table table-condensed">
                <thead>
                    <tr class="cart_menu">
                        <td class="image">Item</td>
                        <td class="description">Name</td>
                        <td class="price">Price</td>
                        <td class="quantity">Quantity</td>
                        <td class="total">Total</td>
                        <td></td>
                    </tr>
                </thead>
                <tbody>
                    {renderData()}
                </tbody>
            </table>

        </div>
    </div>
</section>
)

}
export default Cart;
