import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Edit_product(){

const param = useParams();
let token = localStorage.getItem("token");
let id = localStorage.getItem("auth");
    id = JSON.parse(id)
    let id_user = id.id;
const [img,setImg] = useState([]);
console.log(img)
const [data,setData] = useState({
    Name:"",
    Price:"",
    Category:"",
    Brand:"",
    Status:"",
    Company:"",
    Detail:"",
    Sale:"",
    Image:""

});
useEffect(()=>{
    let config = {
        headers: {
            'Authorization': 'Bearer '+ token,
            'Content_Type': 'multipart/form/data',
            'Accept': 'application/json'
        }
    };
    axios.get("http://localhost:8080/laravel8/laravel8/public/api/user/product/" + param.id, config)
    .then(res=>{
        // console.log(res.data.data)
        setImg(res.data.data)
        let getdata = res.data.data;
        setData({
            Name: getdata.name,
            Price: getdata.price,
            Category: getdata.id_category,
            Brand: getdata.id_brand,
            Status: getdata.status,
            Company: getdata.company_profile,
            Detail: getdata.detail,
            Image: getdata.image,
            Sale: getdata.sale ? getdata.sale : 0,
        })
    });
},[]);

function renderImg(){
    if(Object.keys(img).length >0){
        return Object.keys(img).map((key,index)=>{
            return(
                <img src={"http://localhost:8080/laravel8/laravel8/public/upload/product/" + id_user + "/" + img[key]["image"]}></img>

            )
        })
    }
}


return(
    <div class="signup-form">
        <h2>Edit Product</h2>
        <form >
            <p><input type="text" value={data.Name} placeholder="Name"></input></p>
            <p><input type="text" value={data.Price} placeholder="Price"></input></p>
            <p><input type="text" value={data.Category} placeholder="Category"></input></p>
            <p><input type="text" value={data.Brand} placeholder="Brand"></input></p>
            <p><input type="file" multiple></input></p>
            {renderImg()}
           
            <p><input type="text" value={data.Status} placeholder="Status"></input></p>
            <p><input type="text" value={data.Company} placeholder="Company"></input></p>
            <p><input type="text" value={data.Detail} placeholder="Detail"></input></p>
            <p><input type="text" value={data.Sale} placeholder="Sale"></input></p>
            <button type="submit">Update</button>
        </form>
    </div>
)


}
export default Edit_product;
