import axios from "axios";
import { useEffect, useState } from "react";

function ListComment(){
	let id_blog = localStorage.getItem("Id");

	const [getCmt, setGetCmt] = useState([]);
	console.log(getCmt)
useEffect(()=>{
	axios.get("http://localhost:8080/laravel8/laravel8/public/api/blog/detail/" + id_blog)
	.then(res =>{
		setGetCmt(res.data.data.comment)
	})
	.catch(function(error){
		console.log(error)
	})
},[]);

	function Comments(){
		if((getCmt).length > 0){
			return (getCmt).map((value,key)=>{
					return(
						<li key={key} class="media second-media">
								<a class="pull-left" href="#">
									<img class="media-object" src="images/blog/man-three.jpg" alt=""/>
								</a>
								<div class="media-body">
									<ul class="sinlge-post-meta">
										<li><i class="fa fa-user"></i>{value.name_user}</li>
										<li><i class="fa fa-clock-o"></i>1:33 PM</li>
										<li><i class="fa fa-calendar"></i> {value.updated_at}</li>
									</ul>
									<p>{value.comment}</p>
									<a class="btn btn-primary" href=""><i class="fa fa-reply"></i>Replay</a>
								</div>
						</li>
					)
		})
	}
	
		
	}
	return (
		<div class="response-area">
			<h2>3 RESPONSES</h2>
			<ul class="media-list">
				{Comments()}
			</ul>					
	    </div>
	)
}
export default ListComment;
