import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
function Wishlist(){
    const [data,setData] = useState([]);
    // console.log(data)
    useEffect(()=>{
        axios.get("http://localhost:8080/laravel8/laravel8/public/api/product/wishlist")
        .then(res=>{
            setData(res.data.data)
        });
    },[]);

let WL = {};
function XoaWL(e){
    e.preventDefault();
    let getid= e.target.id;
    if(data.length > 0){
        data.map((value,key)=>{
            if(value.id == getid){
               delete data[key];
            }
        })
    }
    let newArray = data.filter(function(v){return v!==''})
        setData(newArray);
}
// let W_list = localStorage.getItem("W_list");
//     W_list = JSON.parse(W_list)
    function renderData(){
        if(data.length > 0){
            return data.map((value,key)=>{
                let images = data[key]["image"];
                let img = JSON.parse(images);
                return(
                    <div class="col-sm-4">
                        <div class="product-image-wrapper">
                            <div class="single-products">
                                <div class="productinfo text-center">
                                                <img src={"http://localhost:8080/laravel8/laravel8/public/upload/product/" + data[key]["id_user"] + "/" + img[0] } alt="" />
                                                <h2>{data[key]["price"]}</h2>
                                                <p>{data[key]["name"]}</p>
                                                <a href="" class="btn btn-default add-to-cart"><i class="fa fa-shopping-cart"></i>Add to cart</a>
                                </div>
                                <div class="product-overlay">
                                    <div class="overlay-content">
                                        <h2>{data[key]["price"]}</h2>
                                        <p>{data[key]["name"]}</p>
                                        <a href="" class="btn btn-default add-to-cart"><i class="fa fa-shopping-cart"></i>Add to cart</a>
                                    </div>
                                </div>
                            </div>
                            <div class="choose">
                                <ul class="nav nav-pills nav-justified">
                                    <li><a onClick={XoaWL} id={data[key]["id"]} ><i class="fa fa-plus-square"></i>Delete to wishlist</a></li>
                                    <li><a href="#"><i class="fa fa-plus-square"></i>Add to compare</a></li>
                                    <li><Link to={"http://localhost:3000/products/detail/" + data[key]["id"] }>More</Link></li>
                                </ul>
                            </div>
                        </div>
                    </div>
                    
                )
            })
        }
    }
    return(
        <div class="col-sm-9 padding-right">
        <div class="features_items">
        <h2 class="title text-center">Features Items</h2>
        {renderData()}
        </div>
    </div>
    )
}
export default Wishlist;
