import axios from "axios";
import { useEffect, useState } from "react";
import React from "react";
function Add_product(){

const [Category, setCategory] = useState({});
const [Brand, setBrand] = useState([]);

console.log(Category)

useEffect(()=>{
    axios.get("http://localhost:8080/laravel8/laravel8/public/api/category-brand")
    .then(res=>{
        console.log(res.data.category)
        setCategory(res.data.category)
        setBrand(res.data.brand)

    })
    .catch(function (error){
        console.log(error)
        
    })
},[]);
function handleSubmit(e){
    e.preventDefault();

}

function showData(){
    if(Object.keys(Category).length > 0){
        return Object.keys(Category).map((key,index)=>{
            return(
                <select>
                    <option value={Category[key]["id"]}>{Category[key]["category"]}</option>
                </select>
              

            );     
        })
    }
};

    return(
        <div class="signup-form">
            <h2>Creat Product</h2>
            <form onSubmit={handleSubmit}>
                <p><input placeholder="name"></input></p>
                <p><input placeholder="price"></input></p>
                <p>{showData()}</p>
                   
                <p><input type="file" multiple></input></p>
                <p>
                    <select>
                        <option value={1}>New</option>
                        <option value={0}>Sale</option>
                    </select>
                </p>
                <p><input placeholder="Component"></input></p>
                <p><input placeholder="Detail"></input></p>

            </form> 
            <button type="submit">Signup</button>
        </div>
    )
}
export default Add_product;
