import axios from "axios";
import { useEffect, useState } from "react";

function Cart(){
let cart = localStorage.getItem("cart");
    cart = JSON.parse(cart);
    console.log(cart);
const [data,setData] = useState([]);
const [qty,setQty] = useState();
useEffect(()=>{
    axios.post("http://localhost:8080/laravel8/laravel8/public/api/product/cart", cart)
    .then(res=>{
        console.log(res.data.data)
        setData(res.data.data)
    })
},[]);

function renderData(){
    if(data.length > 0){
        return data.map((value,key)=>{
            let img = value.image;
            let images = JSON.parse(img)
            let total = value.price * value.qty;
            function handleQty(){
                setQty(value.qty + 1)
            }
            return(
                <table class="table table-condensed">
                <thead>
                    <tr class="cart_menu">
                        <td class="image">Item</td>
                        <td class="description"></td>
                        <td class="price">Price</td>
                        <td class="quantity">Quantity</td>
                        <td class="total">Total</td>
                        <td></td>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td class="cart_product">
                            <a href={"http://localhost:8080/laravel8/laravel8/public/upload/product/" + value.id_user + "/" + images}><img src="images/cart/one.png" alt=""/></a>
                        </td>
                        <td class="cart_description">
                            <h4><a href="">{value.name}</a></h4>
                            <p>Web ID: 1089772</p>
                        </td>
                        <td class="cart_price">
                            <p>{value.price}</p>
                        </td>
                        <td class="cart_quantity">
                            <div class="cart_quantity_button">
                                <a onClick={handleQty} type="submit" class="cart_quantity_up" href=""> + </a>
                                <input class="cart_quantity_input" type="text" name="quantity" value={qty} autocomplete="off" size="2"/>
                                <a class="cart_quantity_down" href=""> - </a>
                            </div>
                        </td>
                        <td class="cart_total">
                            <p class="cart_total_price">{total}</p>
                        </td>
                        <td class="cart_delete">
                            <a class="cart_quantity_delete" href=""><i class="fa fa-times"></i></a>
                        </td>
                    </tr>
                </tbody>
            </table>
            )
        })
    }
}

return(
    <section id="cart_items">
    <div class="container">
        <div class="breadcrumbs">
            <ol class="breadcrumb">
              <li><a href="#">Home</a></li>
              <li class="active">Shopping Cart</li>
            </ol>
        </div>
        <div class="table-responsive cart_info">
            {renderData()}
        </div>
    </div>
</section>
)

}
export default Cart;
