import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Blog from "./Blog";

function Detail(){
 
const [getdata, setData] = useState([]);
const param = useParams(Blog);
const id = param.id;

useEffect(()=>{
    axios.get("http://localhost/laravel8/laravel8/public/api/blog/detail/" + id)
    .then(res =>{
        setData(res.data)
           
    })
    .catch(function (error) {
        console.log(error)
    })
},[])
function renderData(){
    console.log(getdata)
    if(getdata.length > 0){
        return getdata.map((value, key)=>{
            return (
				<div class="single-blog-post">
							<h3>{value.title}</h3>
							<div class="post-meta">
								<ul>
									<li><i class="fa fa-user"></i> Mac Doe</li>
									<li><i class="fa fa-clock-o"></i> 1:33 pm</li>
									<li><i class="fa fa-calendar"></i> DEC 5, 2013</li>
								</ul>
								<span>
									<i class="fa fa-star"></i>
									<i class="fa fa-star"></i>
									<i class="fa fa-star"></i>
									<i class="fa fa-star"></i>
									<i class="fa fa-star-half-o"></i>
								</span>
							</div>
							<a href="">
								<img src={"http://localhost/laravel8/laravel8/public/upload/Blog/image/" + value.image } alt=""/>
							</a>
							<p>{value.description}</p>
							<Link class="btn btn-primary" to={"http://localhost:3000/blog/detail/" + value.id}>Read More</Link>
				</div>   
						
			)
        })
    }
  
}
    return (
      <div class="col-sm-9">
        <div class="blog-post-area">
            <h2 class="title text-center">Latest From our Blog</h2>
            {renderData()}         
        </div>
      </div>  
    
    )

}
export default Detail;
