import { useEffect, useState } from "react";

function Add_product(){

const [Category, setCategory] = useState([]);
const [Brand, setBrand] = useState([]);
const [Error, setError] = useState("")
console.log(Brand)

useEffect(()=>{
    axios.get("http://localhost:8080/laravel8/laravel8/public/api/category-brand")
    .then(res=>{
        console.log(res)
        setGetInput(res.data.category)
        setBrand(res.data.brand)

    })
    .catch(function (error){
        console.log(error)
        
    })
},[]);
function handleSubmit(){
    if(getInput==""){
        setError("nhap input")
    }
    else{
        setError("")
    }
}
// function handleIp(e){{
//     let getName= e.target.name
//     let getValue = e.target.value
//     setData(state => ({...state,[getName]:getValue}))
// }}
const [getInput,setGetInput] = useState("")
function handleInput(e){
    setGetInput(e.target.value)

}
function showData(){
    if(Category.length > 0){
        return Category.map((value,key)=>{
            return(
                <option value="">Vui lòng chọn</option>
                        
                           
                                
            
            )

        })

    }



        
}

    return(
        <div>
            <form action="#" onSubmit={handleSubmit}>
                <select value={getInput} onChange={handleInput}>
                {showData()}
                </select>
            
           
                
            </form> 
        </div>
    )
}
export default Add_product;
