import StarRatings from 'react-star-ratings';
import { useState } from "react"; 
import axios from 'axios';


    function Rate(){

        let token = localStorage.getItem("token");
        let auth =  localStorage.getItem("auth");
            auth = JSON.parse(auth);
        let id_blog = localStorage.getItem("Id");

        const [rating, setRating] = useState(0);

        function changeRating( newRating, name ) {
          setRating(newRating)
          // - xu ly logic
          let login = localStorage.getItem("checkLogin");
          if(login==null){
            alert("vui long dang nhap de danh gia") 
          }
          else{
             // - xu ly api
            alert("ok")
            let config ={
                headers: {
                    'Authorization': 'Bearer '+ token,
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Accept': 'application/json'
                }
            };

            alert("ok")
            const formData = new FormData();
            formData.append('id_blog', id_blog);
            formData.append('id_user', auth.id);
            formData.append('rate', rating);
          }
          axios.post("http://localhost:8080/laravel8/laravel8/public/api/blog/comment/" + id_blog, formData, config)
                .then((res) =>{
                    if(res.data.errors){
                        setErr(res.data.errors)	
                    }
                })
        }

        return (         
          <StarRatings 
            rating={rating}
            starRatedColor="blue"
            changeRating={changeRating}
            numberOfStars={6}
            name='rating'
          /> 
        ); 
    }
   export default Rate;
